insert into explicacao values(0, 1, 'Primeiramente, antes de começar o level aparecerá uma historinha explicando o que aconteceu com a Matemágika para que o você possa ajudá-la.', 0)
insert into explicacao values(0, 2, 'Depois o jogo te explicará o conteúdo a ser treinado nesse level, isto é, se o level é sobre fração, o jogo explicará como fazer as 4 operações com frações.', 0)
insert into explicacao values(0, 3, 'O objetivo do jogo é chegar até o final da fase tendo matado todos os inimigos.', 0)
insert into explicacao values(0, 4, 'Para matar inimigos você terá que acertá-lo com um poder, que pode ser acionado ao apertar [ESPACO] e responder um quiz em determinado tempo.', 0)
insert into explicacao values(0, 5, 'Lembre-se que você precisa esperar um tempo entre cada poder que é usado.', 0)
insert into explicacao values(0, 6, 'Quando você responde quizzes você ganha xp e moedas, com as quais você pode comprar novos personagens.', 0)
insert into explicacao values(0, 6, 'Enquanto você está respondendo os quizzes, o tempo não para porém fica mais lento.', 0)
insert into explicacao values(0, 7, 'Para andar para os lados e pular, use as setas do teclado. Se clicar duas vezes na seta de cima, o personagem pula mais alto.', 0)
insert into explicacao values(0, 8, 'As duas maneiras de perder o level são: 1) errar dois quizzes serguidos e 2) perder 3 vidas (perde-se uma vida por encostar em um inimigo que está vivo).', 0)
insert into explicacao values(0, 9, 'Ao final de cada level haverá um quiz final, testando mais uma vez os seus conhecimentos na matéria.', 0)
insert into explicacao values(0, 10, 'A resposta desse quiz juntamente com o número de vidas que você está decretarão quantas estrelas você conseguirá naquele level.', 0)
insert into explicacao values(0, 1, 'Esse bloco a sua direita é um Quiz Box. De uma cabeçada na caixa para responder a um quiz. Lembre-se: quizzes dão moedas e xp!', 1)
insert into explicacao values(0, 2, 'Não haverá mais explicações no meio do jogo a partir de agora. BOM JOGO! ', 1)
